const NotImplemented = require('../errors/NotImplemented')
const ParserError = require('../errors/ParserError')

/**
 * Base class for strategies around the Parser
 * Every function that haven't been implemented
 * will throw an NotImplementedError
 *
 * @constructor
 */
function Base () {}

/**
 * Base.prototype.stringify - receives * form of data and turns it into a string
 *
 * @param {*} data
 * @param {object} options
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.stringify = function stringify (data, options) {
  throw new NotImplemented()
}

/**
 * Base.prototype.parse - receives an string and translate it to valid JavaScript
 *
 * @param {string} data
 * @param {object} options
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.parse = function parse (data, options) {
  throw new NotImplemented()
}

/**
 * Base.prototype.pipe - prototype for streams
 *
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.pipe = function pipe () {
  throw new NotImplemented()
}

/**
 * Base.prototype.valid - checks if a given value is valid in desired format using the implemented method parse(), ignoring any exception
 *
 * @param {object} options - any option accepted for the implemented method parse()
 * @returns {boolean} wether or not the given data is a valid format
 */
Base.prototype.valid = function valid (data, options = {}) {
  try {
    this.parse(data, options)
    return true
  } catch (error) {
    if (error instanceof ParserError) {
      return false
    }

    throw error
  }
}

/**
 * Base.prototype.pipeParse - prototype for streams
 *
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.pipeParse = function pipeParse () {
  throw new NotImplemented()
}

/**
 * Base.prototype.pipeStringify - prototype for streams
 *
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.pipeStringify = function pipeStringify () {
  throw new NotImplemented()
}

module.exports = Base

const Base = require('./Base')
const ParserError = require('../errors/ParserError')
const csvParser = require('csv-parse/lib/sync')
const csvStringify = require('csv-stringify/lib/sync')

/**
 * Csv - Support for CSV filetype
 *
 * @constructor
 */
function Csv () {}

Csv.prototype = Object.create(Base.prototype)

/**
 * Csv.prototype.parse - receives an CSV string and returns valid JS
 *
 * @param {string} data
 * @param {object} options
 * @param {(boolean|array|function)} options.headers - If should parse first line as the headers, default is true
 * @param {(string|Buffer)} options.delimiter - Which delimiters to use when parsing, defaults to comma `,`
 * @param {number} options.skipLines - How many lines it should skip before parsing, defaults to 1
 * @param {number} options.offset - How many lines it should parse, defaults to -1
 * @returns {array}
 */
Csv.prototype.parse = function parse (data, options = {}) {
  const config = {
    columns: true,
    skip_empty_lines: true,
    delimiter: options.delimiter || ',',
    from_line: options.skipLines || 1
  }

  if (Object.prototype.hasOwnProperty.apply(options, ['headers'])) {
    config.columns = options.headers
  }

  if (options.offset) {
    config.to_line = options.offset
  }

  try {
    return csvParser(data, config)
  } catch (e) {
    const context = {
      code: e.code,
      message: e.message,
      column: e.column,
      emptyLines: e.empty_lines,
      header: e.header,
      index: e.index,
      lines: e.lines,
      quoting: e.quoting,
      records: e.records
    }

    throw new ParserError('csv', context)
  }
}

/**
 * Csv.prototype.stringify - receives * valid JS data and returns it as CSV
 *
 * @param {array} data
 * @param {object} options
 * @param {boolean} options.headers - If should set first line as the headers, default is true
 * @param {(array|object)} options.columns - Custom column mapping, see examples for more
 * @returns {string}
 */
Csv.prototype.stringify = function stringify (data, options = {}) {
  const config = {
    header: true
  }

  if (options.headers === false) {
    config.header = false
  }

  if (options.columns) {
    config.columns = options.columns
  }

  return csvStringify(data, config)
}

module.exports = Csv

const Base = require('./Base')
const ParserError = require('../errors/ParserError')
const JSONStream = require('JSONStream')

/**
 * Json - Support for JSON filetype
 *
 * @constructor
 */
function Json () {}

Json.prototype = Object.create(Base.prototype)

/**
 * Json.prototype.parse - receives an JSON string and returns valid JS
 *
 * @param {string} data
 * @throws {ParserError} In case the JSON string is not valid, ParserError will be thrown
 * @returns {*}
 */
Json.prototype.parse = function parse (data) {
  try {
    return JSON.parse(data)
  } catch (e) {
    throw new ParserError('json')
  }
}

/**
 * Json.prototype.stringify - receives * valid JS data and returns it as JSON
 *
 * @param {*} data
 * @returns {string}
 */
Json.prototype.stringify = function stringify (data) {
  return JSON.stringify(data)
}

/**
 * Json.prototype.pipeStringify - helps to stream object or array into JSON valid data
 *
 * @param {object} [config] - sets config for stream
 * @param {string} [config.type='array'] - which type of data you're streaming, defaults do array
 * @returns {WritableStream}
 */
Json.prototype.pipeStringify = function pipeStringify (config = {}) {
  config.type = config.type || 'array'
  const streams = {
    object: JSONStream.stringifyObject,
    array: JSONStream.stringify
  }

  const fn = streams[config.type]

  if (!fn) {
    throw new ParserError(`Supplied type "${config.type}" is not allowed. Use either "array" or "object"`)
  }

  return fn()
}

/**
 * Json.prototype.pipeStringify - helps to stream JSON data to JS
 *
 * @param {object} [config] - sets config for stream
 * @param {string} [config.path] - select which data path to be parsed from JSON to JS
 * @returns {Stream}
 */
Json.prototype.pipeParse = function pipeParse (config) {
  return JSONStream.parse()
}

module.exports = Json

