/**
 * Parser - Receives any strategy and safely implement it
 *
 * @constructor
 * @param {Base} strategy - Any strategy implementing the Base's prototype
 */
function Parser (strategy) {
  this.strategy = strategy
}

/**
 * Parser.prototype.parse - Exposes the parsing from string to any valid JS type with the strategy
 *
 * @param {string} data
 * @param {object} options
 */
Parser.prototype.parse = function parse (data, options) {
  return this.strategy.parse(data, options)
}

/**
 * Parser.prototype.stringify - Exposes the stringify() method from any valid JS type to expected type with the strategy
 *
 * @param {*} data
 * @param {object} options
 */
Parser.prototype.stringify = function stringify (data, options) {
  return this.strategy.stringify(data, options)
}

Parser.prototype.get = function get (data, path) {}

Parser.prototype.has = function has (data, path) {}

module.exports = Parser

/**
 * NotImplemented
 */
function NotImplemented () {
  this.name = 'NotImplemented'
  this.message = 'This method haven\'t been implemented yet'
}

NotImplemented.prototype = new Error()

module.exports = NotImplemented

/**
 * ParseError
 */
function ParseError (format) {
  this.name = 'ParseError'
  this.message = `Failed to parse ${format}`
}

ParseError.prototype = new Error()

module.exports = ParseError

const {
  Json,
  Xml,
  Yaml,
  Csv
} = require('./strategies')
const Parser = require('./Parser')

module.exports = {
  json: new Parser(new Json()),
  xml: new Parser(new Xml()),
  yaml: new Parser(new Yaml()),
  csv: new Parser(new Csv())
}

const NotImplemented = require('../errors/NotImplemented')

/**
 * Base class for strategies around the Parser
 * Every function that haven't been implemented
 * will throw an NotImplementedError
 *
 * @constructor
 */
function Base () {}

/**
 * Base.prototype.stringify - receives * form of data and turns it into a string
 *
 * @param {*} data
 * @param {object} options
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.stringify = function stringify (data, options) {
  throw new NotImplemented()
}

/**
 * Base.prototype.parse - receives an string and translate it to valid JavaScript
 *
 * @param {string} data
 * @param {object} options
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.parse = function parse (data, options) {
  throw new NotImplemented()
}

/**
 * Base.prototype.pipe - prototype for streams
 *
 * @throws {NotImplemented} This method must be implemented
 */
Base.prototype.pipe = function pipe () {
  throw new NotImplemented()
}

module.exports = Base

const Base = require('./Base')
// const ParserError = require('../errors/ParserError')
const csvParser = require('csv-parse/lib/sync')
const csvStringify = require('csv-stringify/lib/sync')

/**
 * Csv - Support for CSV filetype
 *
 * @constructor
 */
function Csv () {}

Csv.prototype = Object.create(Base.prototype)

/**
 * Csv.prototype.parse - receives an CSV string and returns valid JS
 *
 * @param {string} data
 * @param {object} options
 * @param {boolean} options.headers - If should parse first line as the headers, default is true
 * @param {(string|Buffer)} options.delimiter - Which delimiters to use when parsing, defaults to comma `,`
 * @param {number} options.skipLines - How many lines it should skip before parsing, defaults to 1
 * @param {number} options.offset - How many lines it should parse, defaults to -1
 * @returns {array}
 */
Csv.prototype.parse = function parse (data, options = {}) {
  const config = {
    columns: true,
    skip_empty_lines: true,
    delimiter: options.delimiter || ',',
    from_line: options.skipLines || 1
  }

  if (options.headers === false) {
    config.columns = false
  }

  if (options.offset) {
    config.to_line = options.offset
  }

  return csvParser(data, config)
}

/**
 * Csv.prototype.stringify - receives * valid JS data and returns it as CSV
 *
 * @param {array} data
 * @param {object} options
 * @param {boolean} options.headers - If should set first line as the headers, default is true
 * @param {(array|object)} options.columns - Custom column mapping, see examples for more
 * @returns {string}
 */
Csv.prototype.stringify = function stringify (data, options = {}) {
  const config = {
    header: true
  }

  if (options.headers === false) {
    config.header = false
  }

  if (options.columns) {
    config.columns = options.columns
  }

  return csvStringify(data, config)
}

module.exports = Csv

const Base = require('./Base')
const ParserError = require('../errors/ParserError')

/**
 * Json - Support for JSON filetype
 *
 * @constructor
 */
function Json () {}

Json.prototype = Object.create(Base.prototype)

/**
 * Json.prototype.parse - receives an JSON string and returns valid JS
 *
 * @param {string} data
 * @throws {ParserError} In case the JSON string is not valid, ParserError will be thrown
 * @returns {*}
 */
Json.prototype.parse = function parse (data) {
  try {
    return JSON.parse(data)
  } catch (e) {
    throw new ParserError('json')
  }
}

/**
 * Json.prototype.stringify - receives * valid JS data and returns it as JSON
 *
 * @param {*} data
 * @returns {string}
 */
Json.prototype.stringify = function stringify (data) {
  return JSON.stringify(data)
}

module.exports = Json

const Base = require('./Base')
const ParserError = require('../errors/ParserError')
const xml = require('xml-js')

/**
 * Xml - Support for XML filetype
 *
 * @constructor
 */
function Xml () {
  this.XML_VERSION_TAG = {
    _declaration: {
      _attributes: {
        version: '1.0',
        encoding: 'utf-8'
      }
    }
  }
}

Xml.prototype = Object.create(Base.prototype)

/**
 * Xml.prototype.setXmlDeclaration - sets XML declaration tag on first position of array or object
 *
 * @param {(object|array)} data - input data
 * @returns {(object|array)}
 */
Xml.prototype.setXmlDeclaration = function setXmlDeclaration (data) {
  if (Array.isArray(data)) {
    data = [this.XML_VERSION_TAG, ...data]
  } else {
    data = { ...this.XML_VERSION_TAG, ...data }
  }

  return data
}

/**
 * Xml.prototype.stringify - receives * valid JS data and returns it as XML
 *
 * @param {(object|array)} data
 * @param {Object} options - options for turning JS data into XML
 * @param {boolean} options.ignoreDeclaration - don't output XML version tag, default is true
 * @example
 * // returns '<?xml version="1.0" encoding="utf-8"?><package>parser</package>'
 * const data = { package: 'parser' }
 * Xml().stringify(data)
 * @example
 * // returns '<package>parser</package>'
 * const data = { package: 'parser' }
 * Xml().stringify(data, { ignoreDeclaration: true })
 * @returns {string}
 */
Xml.prototype.stringify = function stringify (data, options = {}) {
  const config = {
    compact: true,
    ignoreDeclaration: false
  }

  data = this.setXmlDeclaration(data)

  if (options.ignoreDeclaration) {
    config.ignoreDeclaration = true
  }

  return xml.js2xml(data, config)
}

/**
 * Xml.prototype.parse - receives an XML string and translate it to valid JavaScript
 *
 * @param {string} data
 * @param {object} options
 * @param {object} options.showDeclaration - force parsing XML declaration tag
 * @throws {NotImplemented} This method must be implemented
 */
Xml.prototype.parse = function parse (data, options = {}) {
  try {
    const config = {
      compact: true,
      ignoreDeclaration: true,
      nativeType: true,
      nativeTypeAttributes: true
    }

    if (options.showDeclaration) {
      config.ignoreDeclaration = false
    }

    return xml.xml2js(data, config)
  } catch (error) {
    throw new ParserError(error.message)
  }
}

module.exports = Xml

